import { useParams } from "react-router";
import React, { useState, useEffect } from "react";
import axios from "axios";
import CardMeal from "../components/CardMeal";
import "../styles/FavoriteRecipes.css";

const FavoriteRecipes = () => {
  const { id } = useParams();
  const [favrecipes, setFavRecipes] = useState([]);

  useEffect(() => {
    axios
      .get(`https://guarded-peak-18752.herokuapp.com/favorites/${id}`)
      .then((res) => {
        setFavRecipes(res.data);
        console.log(favrecipes);
      });
  }, []);

  const deleteRecipe = (recipe_id) => {
    axios
      .delete(`https://guarded-peak-18752.herokuapp.com/favorites/${recipe_id}`)
      .then((res) => {
        const removedArr = [...favrecipes].filter(
          (recipe) => recipe.recipe_id !== recipe_id
        );
        setFavRecipes(removedArr);
      });
  };

  const FavRecipesList = favrecipes.map((recipe) => {
    return (
      <div id="favcard">
        {" "}
        <button
          id="btn_small"
          onClick={() => {
            deleteRecipe(recipe.recipe_id);
          }}
        >
          X
        </button>{" "}
        {/* pass the id param to the delete recipe by recipe_id*/}
        <CardMeal
          image={recipe.recipe_image}
          name={recipe.recipe_name}
          link={recipe.recipe_url}
        />
      </div>
    );
  });

  return (
    <div className="favorites">
      <h2>These are your saved recipes:</h2>
      <div className="favoritelist">{FavRecipesList}</div>;
    </div>
  );
};

export default FavoriteRecipes;
