import { useParams } from "react-router";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/FavoriteRecipes.css";

const FavoriteRecipes = () => {
  const { id } = useParams();
  const [favrecipes, setFavRecipes] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3000/favorites/${id}`).then((res) => {
      setFavRecipes(res.data);
      console.log(favrecipes);
    });
  }, []);

  const deleteRecipe = (recipe_id) => {
    axios.delete(`http://localhost:3000/favorites/${recipe_id}`).then((res) => {
      const removedArr = [...favrecipes].filter((recipe) => recipe.id !== id);
      setFavRecipes(removedArr);
    });
  };

  const FavRecipesList = favrecipes.map((recipe, i) => {
    return (
      <div id="card">
        <div className="slide">
          <div className="content">
            <img src={recipe.recipe_image} alt="recipe_image" />
          </div>
        </div>
        <div className="slide slide2">
          <div className="content">
            <h3>{recipe.recipe_name}</h3>
            <a href={recipe.recipe_url} target="_blank noreferrer">
              <button id="btn" type="button">
                Recipe
              </button>
            </a>
            <button
              onClick={() => {
                deleteRecipe(recipe.recipe_id);
              }}
            >
              Delete
            </button>{" "}
            {/* pass the id param to the delete recipe by recipe_id*/}
          </div>
        </div>
      </div>
    );
  });

  return (
    <div className="favorites">
      <h2>These are your saved recipes:</h2>
      <div className="favoritelist">{FavRecipesList}</div>;
    </div>
  );
};

export default FavoriteRecipes;
