import SigninForm from "../components/SigninForm";
import RegisterForm from "../components/RegisterForm";
import MealPlanner from "./MealPlanner";
import FavoriteRecipes from "./FavoriteRecipes";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Signin = () => {
  const [route, setRoute] = useState("signin"); // The authentification will be determine by the route state. Signin means that the user has to sign in.
  const [signInEmail, setSignInEmail] = useState("");
  const [signInPassword, setSigninPassword] = useState("");
  const [user, loadUser] = useState({
    id: "",
    name: "",
    email: "",
    favorites: "",
    joined: "",
  });

  const updateLoadUser = (data) => {
    //this is for when to update the profile informations when we register or signin
    loadUser({
      id: data.id,
      name: data.name,
      email: data.email,
      favorites: data.entries,
      joined: data.joined,
    });
  };

  useEffect(() => {
    const localdata = localStorage.getItem("save-status");
    if (localdata) {
      setRoute(JSON.parse(localdata));
    }
  }, []);

  //to add the state route into local storage has a string!
  useEffect(() => {
    localStorage.setItem("save-status", JSON.stringify(route));
  });

  //to change the state to what the user writes in the signin form
  const onEmailChange = (event) => {
    setSignInEmail(event.target.value);
  };

  const onPasswordChange = (event) => {
    setSigninPassword(event.target.value);
  };

  //this will change the route state, that will determine the validation to show mealplan ou not
  const onRouteChange = (route) => {
    setRoute(route);
  };

  //When submit, we will connect to the server to post the email and password
  const onSubmitSignIn = (e) => {
    e.preventDefault(); //prevent the default submit event. Don't forget to put also on e param on the onSubmitSignIn - SigninForm
    fetch("http://localhost:3000/signin", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        email: signInEmail,
        password: signInPassword,
      }),
    })
      .then((response) => response.json())
      .then((user) => {
        //after we post, if it matches with the email and password database, the response from the server will be user details. So if we receive an id that means it was the post === database
        if (user.id) {
          updateLoadUser(user); //to update user details
          setRoute("mealplanner"); // to change state from signin to mealplanner and show the mealplan
        }
      });
  };

  return (
    <div>
      {route === "mealplanner" ? (
        <div>
          {/* Added a signout option that will change the state to sigin. This means that mealplanner will not be shown */}
          <p>Hello, {user.name}!</p>
          <p onClick={() => onRouteChange("signin")}>Signout</p>

          <Link to={`/favorites/${user.id}`}>
            <button type="button"> Click Me Favorites!</button>
          </Link>
          <MealPlanner user={user.id} />
        </div>
      ) : route === "signin" ? ( // If the route state is "signin" it will show only the sign in form. If the route is not signin, that means the user is logged so the mealplanner will be shown
        <SigninForm
          onSubmitSignIn={onSubmitSignIn}
          onEmailChange={onEmailChange}
          onPasswordChange={onPasswordChange}
          onRouteChange={onRouteChange}
        />
      ) : (
        <RegisterForm
          updateLoadUser={updateLoadUser}
          onRouteChange={onRouteChange}
        />
      )}
    </div>
  );
};

export default Signin;
