import { useParams } from "react-router";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/FavoriteRecipes.css";

const UserFavorites = () => {
  const { id } = useParams();
  const [favrecipes, setFavRecipes] = useState([]);

  useEffect(() => {
    axios.get(`https://guarded-peak-18752/favorites/${id}`).then((res) => {
      setFavRecipes(res.data);
      console.log(favrecipes);
    });
  }, []);

  const deleteRecipe = (recipe_id) => {
    axios
      .delete(`https://guarded-peak-18752/favorites/${recipe_id}`)
      .then((res) => {
        const removedArr = [...favrecipes].filter(
          (recipe) => recipe.recipe_id !== recipe_id
        );
        setFavRecipes(removedArr);
      });
  };

  const FavRecipesList = favrecipes.map((recipe) => {
    return (
      <div id="favcard">
        {" "}
        <button
          id="btn_small"
          onClick={() => {
            deleteRecipe(recipe.recipe_id);
          }}
        >
          X
        </button>{" "}
        {/* pass the id param to the delete recipe by recipe_id*/}
        <div className="card">
          <div className="slide1">
            <div className="content">
              <img src={recipe.recipe_image} alt="recipe_image" />
            </div>
          </div>
          <div className="slide2">
            <div className="content">
              <h3>{recipe.recipe_name} </h3>
              <a href={recipe.recipe_url} target="_blank noreferrer">
                <button id="btn_home" type="button">
                  Recipe
                </button>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  });

  return (
    <div className="favorites">
      <div className="favoritelist">{FavRecipesList}</div>;
    </div>
  );
};

export default UserFavorites;
