import React from "react";
import { Form } from "react-bootstrap";
import "../styles/Filter.css";

const Filter = ({ handleChange }) => {
  return (
    <>
      <h1>Weekly Meal Plan Generator</h1>
      <p> Check your recipes ideas for this week! </p>
      <form>
        <div className="option">
          <input
            type="radio"
            id="inputbox"
            name="diet"
            value="Standard"
            //checked //by default it will be a standard diet so we will not change the state
          ></input>
          <label className="labelCheckbox" for="diet">
            {" "}
            Standard
          </label>
        </div>
        <div className="option">
          <input
            type="radio"
            id="inputbox"
            name="diet"
            value="Vegetarian"
            onChange={handleChange}
          ></input>
          <label className="labelCheckbox" for="diet">
            {" "}
            Vegetarian
          </label>
        </div>
        <div className="option">
          <input
            type="radio"
            id="inputbox"
            name="diet"
            value="Vegan"
            onChange={handleChange}
          ></input>
          <label className="labelCheckbox" for="diet">
            {" "}
            Vegan
          </label>
        </div>
      </form>
    </>
  );
};

export default Filter;
