import "../styles/CardBeer.css";

const CardBeer = ({ image, name, description, user }) => {
  const onSaveRecipe = () => {
    fetch("https://guarded-peak-18752.herokuapp.com/favorites", {
      method: "put",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: user,
        beer_name: name,
        beer_description: description,
        beer_image: image,
      }),
    })
      .then((response) => response.json())
      .then((res) => {
        console.log(res);
      });
  };

  return (
    <div className="card">
      <div className="slide1">
        <div className="content">
          <img src={image} alt="recipe_image" />
        </div>
      </div>
      <div className="slide2">
        <div className="content">
          <h3>{name} </h3>
          <p>{description}</p>
          <button id="btn_small" type="button">
            <i class="fas fa-heart" onClick={() => onSaveRecipe()}></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default CardBeer;
