import "../styles/CardMeal.css";

const CardMeal = ({ image, name, link, user }) => {
  const onSaveRecipe = () => {
    fetch("https://guarded-peak-18752.herokuapp.com/favorites", {
      method: "put",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: user,
        recipe_name: name,
        recipe_url: link,
        recipe_image: image,
      }),
    })
      .then((response) => response.json())
      .then((res) => {
        console.log(res);
      });
  };

  return (
    <article class="br2 ba dark-gray b--black-10 mv3 w-100 w-50-m w-25-l mw4 center">
      <img
        src={image}
        class="db w-100 br2 br--top"
        alt="Photo of a kitten looking menacing."
      />
      <div class="pa2 ph3-ns pb3-ns">
        <div class="dt w-100 mt1">
          <div class="dtc">
            <p class="f5 f4-ns mv0">{name}</p>
          </div>
          <div class="dtc tr">
            <button id="btncircle" type="button">
              <i class="fas fa-heart" onClick={() => onSaveRecipe()}></i>
            </button>
          </div>
        </div>
        <a href={link} target="_blank noreferrer">
          <button id="btn" type="button">
            Recipe
          </button>
        </a>
      </div>
    </article>
  );
};

export default CardMeal;
