import React, { useState, useEffect } from "react";
import axios from "axios";
//import CardMeal from "./CardMeal";
import "./MealPlanner.css";
//import Filter from "./Filter";
import MealList from "./MealList";

const API_ID = process.env.REACT_APP_EDAMAM_API_ID;
const API_KEY = process.env.REACT_APP_EDAMAM_API_KEY;

const MealPlanner = () => {
  const [breakfast, setBreakfast] = useState([]);
  const [lunch, setLunch] = useState([]);
  const [dinner, setDinner] = useState([]);
  const [diet, setDiet] = useState("balanced");
  const [numberMeals, SetNumberMeals] = useState("40");

  useEffect(() => {
    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=balanced&from=0&to=10&mealType=breakfast`
      )
      .then((res) => {
        setBreakfast(res.data.hits);
      });

    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=balanced&from=0&to=10&dishType=main&mealType=lunch`
      )
      .then((res) => {
        setLunch(res.data.hits);
      });

    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=balanced&from=0&to=10&mealType=dinner&dishType=main`
      )
      .then((res) => {
        setDinner(res.data.hits);
      });
  }, []);

  //Adding user preferences
  //onHandleRadioButton = (e) => {
  //  this.setState({ diet: e.target.value });
  // };

  //displaying recipes per type of meal

  return (
    <div className="meal-plan">
      <h2>breakfast</h2>
      <div className="name">{breakfast && <MealList props={breakfast} />}</div>
      <h2>lunch</h2>
      <div className="name">{breakfast && <MealList props={lunch} />}</div>
      <h2>dinner</h2>
      <div className="name">{breakfast && <MealList props={dinner} />}</div>
    </div>
  );
};

export default MealPlanner;

