import React, { Component } from "react";
import axios from "axios";
import RecipeCard from "./RecipeCard";
import "./MealPlanner.css";

const API_ID = "669ffd56";
const API_KEY = "99bbd34468d8bd690f225c2ec33741af";

function shuffleArray(array) {
  let i = array.length - 1;
  for (; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}

class MealPlan extends Component {
  constructor() {
    super();
    this.state = {
      recipes: [],
      diet: "balanced",
      mealType: "breakfast",
      numberMeals: "7",
    };
  }

  //Fetch API with balanced diet recipes
  componentDidMount() {
    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=${this.state.diet}&mealType=${this.state.mealType}&from=0&to=${this.state.numberMeals}`
      )
      .then((res) => {
        console.log(res);
        this.setState({
          recipes: res.data.hits,
        });
      });
  }

  //displaying recipes per week
  render() {
    const { recipes } = this.state;
    const shuffleRecipeList = shuffleArray(recipes);
    const recipeList = recipes.length ? (
      shuffleRecipeList.map((recipe) => {
        return (
          <RecipeCard
            image={recipe.recipe.image}
            name={recipe.recipe.label}
            link={recipe.recipe.url}
            ingredients={recipe.recipe.ingredientLines}
          />
        );
      })
    ) : (
      <div>
        {" "}
        <p>Meals are cooking...</p>{" "}
      </div>
    );
    return (
      <div>
        <h1>Meal Planner Happiness</h1>
        <div className="mealtypes">
          {" "}
          <h2>Meals</h2>
          <h2>Breakfast</h2>
          <h2>Lunch</h2>
          <h2>Dinner</h2>
        </div>
        <div className="plan_container">
          <div className="days">
            <h2>Monday</h2>
            <h2>Monday</h2>
            <h2>Tuesday</h2>
            <h2>Wednesday</h2>
            <h2> Thursday</h2>
            <h2>Friday</h2>
            <h2>Saturday</h2>
            <h2>Sunday</h2>
          </div>
          <div className="meals">
            <div className="breakfast">{recipeList}</div>
            <div className="breakfast">{recipeList}</div>
            <div className="breakfast">{recipeList}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default MealPlan;
