import CardMeal from "./CardMeal";

function shuffleArray(array) {
  let i = array.length - 1;
  for (; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}

const MealList = ({ props }) => {
  const shuffleMealList = shuffleArray(props); //Since we are display the recipe array elements, i shuffled the array to not show always the same order
  const sevenDaysMeal = shuffleMealList.slice(0, 7); // I asked for 40 recipes and I only want to display 7
  const finalMealList = sevendaysMeal.map((recipe) => {
    return (
      <CardMeal
        image={recipe.recipe.image}
        name={recipe.recipe.label}
        link={recipe.recipe.url}
        ingredients={recipe.recipe.ingredientLines}
      />
    );
  });
  return <div>{finalMealList}</div>;
};

export default MealList;
