import { useParams } from "react-router";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/FavoriteRecipes.css";

const UserFavorites = () => {
  const { id } = useParams();
  const [favbeers, setFavBeers] = useState([]);

  useEffect(() => {
    axios
      .get(`https://guarded-peak-18752.herokuapp.com/favorites/${id}`)
      .then((res) => {
        setFavBeers(res.data);
        console.log(favbeers);
      });
  }, []);

  const deleteBeer = (recipe_id) => {
    axios
      .delete(`https://guarded-peak-18752.herokuapp.com/favorites/${recipe_id}`)
      .then((res) => {
        const removedArr = [...favbeers].filter(
          (recipe) => recipe.recipe_id !== recipe_id
        );
        setFavBeers(removedArr);
      });
  };

  const FavBeerList = favbeers.map((recipe) => {
    return (
      <div id="favcard">
        {" "}
        <button
          id="btn_small"
          onClick={() => {
            deleteBeer(recipe.recipe_id);
          }}
        >
          X
        </button>{" "}
        {/* pass the id param to the delete recipe by recipe_id*/}
        <div className="card">
          <div className="slide1">
            <div className="content">
              <img src={recipe.recipe_image} alt="recipe_image" />
            </div>
          </div>
          <div className="slide2">
            <div className="content">
              <h3>{recipe.recipe_name} </h3>
              <p>{recipe.recipe_url}</p>
            </div>
          </div>
        </div>
      </div>
    );
  });

  return (
    <div className="favorites">
      <div className="favoritelist">{FavBeerList}</div>;
    </div>
  );
};

export default UserFavorites;
