import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import Aos from "aos";
import "../styles/Home.css";
import "aos/dist/aos.css";
import person from "../img/person.jpg";

const Home = () => {
  useEffect(() => {
    Aos.init({ duration: 800 });
  }, []);

  return (
    <div id="home_container">
      <div className="text_container">
        <div className="text">
          <ul>
            <li>
              {" "}
              <h2>
                {" "}
                Get your meal ideas <br />
                for the whole week
              </h2>
            </li>
            <li>
              {" "}
              <p> With the Deliss Weekly Meal Plan Generator</p>
            </li>
            <li>
              {" "}
              <a href="#about_container">
                <button id="btn_home">Learn More</button>
              </a>
            </li>
          </ul>
        </div>
      </div>
      <div data-aos="fade-right" id="about_container">
        <div className="about_card">
          <img src={person} alt="about_image" />
        </div>
        <div className="about_card">
          <div className="about_text">
            <h2> What is Deliss?</h2>

            <p>
              Who said that cooking is boring? <br />
              Deliss is an automatic weekly meal plan generator that gives you
              meal ideas for breakfast, lunch and dinner. 3 recipes for each day
              of the week! You can also save your favorite ideas in your acount.
              Try it!
            </p>
            <h2> How it works?</h2>

            <ol>
              <li>Register or Sign In</li>
              <li>Access the Weekly Meal Planner Generator</li>
              <li>Save your favorite recipes!</li>
            </ol>
            <Link to="/signin">
              <button id="btn_home" type="button">
                {" "}
                Get Started
              </button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
