import "../styles/CardMeal.css";

const CardMeal = ({ image, name, link, user }) => {
  const onSaveRecipe = () => {
    fetch("http://localhost:3000//favorites", {
      method: "put",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        id: user,
        recipe_name: name,
        recipe_url: link,
        recipe_image: image,
      }),
    })
      .then((response) => response.json())
      .then((res) => {
        console.log(res);
      });
  };

  return (
    <div className="card">
      <div className="slide1">
        <div className="content">
          <img src={image} alt="recipe_image" />
        </div>
      </div>
      <div className="slide2">
        <div className="content">
          <h3>{name} </h3>
          <a href={link} target="_blank noreferrer">
            <button id="btn_home" type="button">
              Recipe
            </button>
          </a>
          <button id="btn_small" type="button">
            <i class="fas fa-heart" onClick={() => onSaveRecipe()}></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default CardMeal;
