import { useParams } from "react-router";
import React, { useState, useEffect } from "react";
import axios from "axios";
import CardMeal from "../components/CardMeal";

const FavoriteRecipes = () => {
  const { id } = useParams();
  const [favrecipes, setFavRecipes] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3000/favorites/${id}`).then((res) => {
      setFavRecipes(res.data);
      console.log(favrecipes);
    });
  }, []);

  const FavRecipesList = favrecipes.map((recipe, i) => {
    return (
      <CardMeal
        name={favrecipes[i].recipe_name}
        link={favrecipes[i].recipe_url}
        image={favrecipes[i].recipe_image}
      />
    );
  });

  return (
    <div>
      <h2>these are the favorites {id}</h2>
      <div>{FavRecipesList}</div>;
    </div>
  );
};

export default FavoriteRecipes;
