import React, { useState, useEffect } from "react";
import axios from "axios";
import Filter from "../components/Filter";
import MealList from "../components/MealList";
import "../styles/MealPlanner.css";

const API_ID = process.env.REACT_APP_EDAMAM_API_ID;
const API_KEY = process.env.REACT_APP_EDAMAM_API_KEY;

// We will display a weekly meal planner with breakfast, lunch and dinner.
//We will get the recipes on the edamame API
const MealPlanner = ({ user }) => {
  const [breakfast, setBreakfast] = useState(null);
  const [lunch, setLunch] = useState(null);
  const [dinner, setDinner] = useState(null);
  const [diet, setDiet] = useState(""); //by default the diet will be standard

  //NAO ESQUECER DE ATUALIZAR O NUMERO DE RECEITAS PARA 40!!!!!!
  const URL = `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&healthLabels=${diet}&from=0&to=20&`;

  useEffect(() => {
    fetch(URL + `mealType=breakfast`, {
      method: "GET",
      mode: "cors",
      cache: "no-cache",
    })
      .then((response) => response.json())
      .then((res) => {
        setBreakfast(res.data.hits);
      })
      .catch(console.log);

    axios
      .get(URL + `dishType=main&mealType=lunch`)
      .then((res) => {
        setLunch(res.data.hits);
      })
      .catch(console.log);

    axios
      .get(URL + `dishType=main&mealType=dinner`)
      .then((res) => {
        setDinner(res.data.hits);
      })
      .catch(console.log);
  }, [diet]); //it will only render if the user filters the diet type

  return (
    <div className="plan_container">
      <Filter //Added a filter option for standard, vegetarian and vegan
        handleChange={(e) => {
          setDiet(e.target.value);
        }}
      />

      <h2>Breakfast</h2>
      <div id="breakfast">
        {/* Recipes will be displayed by the MealList component, passing breakfast,lunch and dinner arrays as props */}
        {/* Conditional Rendering.Only output MealList when we have a value for breakfast. */}
        {breakfast && <MealList props={breakfast} diet={diet} user={user} />}
      </div>
      <h2>Lunch</h2>
      <div id="lunch">{lunch && <MealList props={lunch} user={user} />}</div>
      <h2>Dinner</h2>
      <div id="dinner">{dinner && <MealList props={dinner} user={user} />}</div>
    </div>
  );
};

export default MealPlanner;

