import React, { Component } from "react";
import axios from "axios";
import { BreakfastCard, LunchCard, DinnerCard } from "./RecipeCard";
import "./MealPlanner.css";

const API_ID = "669ffd56";
const API_KEY = "99bbd34468d8bd690f225c2ec33741af";

// We have to display 7 breakfasts, 7 lunches and 7 dinners;
// Each of this 7 meals have to change the recipe and order when reset:
// ex:          [banana, apple, orange, durian, kiwi, cherry, lemon] -> then Reset ->
//               [ apple, kiwi, banana, orange, lemon, cherry, durian] -> but are we going to show only 7 recipes? Well we can get more
//                                                                        elements from the API and then splice after with 7:
//              1º get 20 recipes
//              2º Shuffle the 20 recipes
//              3º Get the first 7 recipes from that shuffled array

// Function to shuffle arrays
function shuffleArray(array) {
  let i = array.length - 1;
  for (; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}

class MealPlan extends Component {
  //default meal plan
  constructor() {
    super();
    this.state = {
      breakfast: [],
      lunch: [],
      diet: "balanced",
      mealType: "breakfast",
      numberMeals: "30",
    };
  }

  //Fetch API with balanced diet recipes
  componentDidMount() {
    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=${this.state.diet}&mealType=breakfast&from=0&to=${this.state.numberMeals}`
      )
      .then((res) => {
        console.log(res);
        this.setState({
          breakfast: res.data.hits,
        });
      });

    axios
      .get(
        `https://api.edamam.com/search?app_id=${API_ID}&app_key=${API_KEY}&q=&diet=${this.state.diet}&mealType=lunch&from=0&to=${this.state.numberMeals}`
      )
      .then((res) => {
        console.log(res);
        this.setState({
          lunch: res.data.hits,
        });
      });
  }

  //displaying recipes per MEAL
  render() {
    const { breakfast, lunch } = this.state;

    const shuffleBreakfastList = shuffleArray(breakfast); //Since we are display the recipe array elements, i shuffled the array to not show always the same order
    const weeklyBreakfastNumber = shuffleBreakfastList.slice(0, 7); // I asked for 20 recipes and I only want to display 7
    const BreakfastCardList = breakfast.length ? (
      weeklyBreakfastNumber.map((recipe) => {
        return (
          <BreakfastCard
            image={recipe.recipe.image}
            name={recipe.recipe.label}
            link={recipe.recipe.url}
            ingredients={recipe.recipe.ingredientLines}
          />
        );
      })
    ) : (
      <div>
        {" "}
        <p>Meals are cooking...</p>{" "}
      </div>
    );

    const shuffleLunchList = shuffleArray(lunch); //Since we are display the recipe array elements, i shuffled the array to not show always the same order
    const weeklyLunchNumber = shuffleLunchList.slice(0, 7); // I asked for 20 recipes and I only want to display 7
    const LunchCardList = lunch.length ? (
      weeklyLunchNumber.map((recipe) => {
        return (
          <BreakfastCard
            image={recipe.recipe.image}
            name={recipe.recipe.label}
            link={recipe.recipe.url}
            ingredients={recipe.recipe.ingredientLines}
          />
        );
      })
    ) : (
      <div>
        {" "}
        <p>Meals are cooking...</p>{" "}
      </div>
    );

    return (
      <div>
        <h1>Meal Planner Happiness</h1>
        <div className="mealtypes">
          {" "}
          <h2>Meals</h2>
          <h2>Breakfast</h2>
          <h2>Lunch</h2>
          <h2>Dinner</h2>
        </div>
        <div className="plan_container">
          <div className="days">
            <h2>Monday</h2>
            <h2>Monday</h2>
            <h2>Tuesday</h2>
            <h2>Wednesday</h2>
            <h2> Thursday</h2>
            <h2>Friday</h2>
            <h2>Saturday</h2>
            <h2>Sunday</h2>
          </div>
          <div className="meals">
            <div id="breakfast">{BreakfastCardList}</div>
            <div id="lunch">{LunchCardList}</div>
            <div id="dinner">{BreakfastList}</div>
          </div>
        </div>
      </div>
    );
  }
}

export default MealPlan;
