import "../styles/SignIn.css";

const SigninForm = ({
  onRouteChange,
  onEmailChange,
  onPasswordChange,
  onSubmitSignIn,
}) => {
  return (
    <div className="auth">
      <form>
        <h2>Sign In</h2>
        <label for="email-address">Email</label>
        <input
          type="email"
          name="email-address"
          id="email-address"
          onChange={onEmailChange}
        />
        <label for="password">Password</label>
        <input
          type="password"
          name="password"
          id="password"
          onChange={onPasswordChange}
        />
        <input // when submit Signin it will change the state from signin to mealplanner so that the mealplanner in order to access the mealplan
          onClick={(e) => onSubmitSignIn(e)}
          type="submit"
          value="Sign in"
        />
        <p // when submit Signin it will change to register to show the registerForm
          onClick={() => onRouteChange("register")}
        >
          Register
        </p>
      </form>
    </div>
  );
};

export default SigninForm;
